import {TrashIcon} from "@heroicons/react/24/solid";
import {useContext} from "react";
import {StoreContextUser} from "../Contenxt/authContext";
import Product from "./Product";

const Cart = () => {
  const {products} = useContext(StoreContextUser);
  console.log(products);

  return (
   <div>
     {products && products.map((product) => (
      <div key={product.id} className="max-w-sm mx-auto bg-white rounded-md overflow-hidden shadow-md">
        <img className="w-full h-48 object-cover" src={product.image} alt={product.title}/>

        <div className="p-4">
          <h2 className="text-xl font-semibold mb-2">{product.title}</h2>
          <p className="text-gray-700 mb-2">Prezzo: ${product.price}</p>

          <div className="flex items-center mb-4">
            <label className="text-gray-600 mr-2">Quantit√†:</label>
            <input type="number" className="border rounded-md px-2 py-1 w-16 text-center" value={product.quantity}/>
          </div>

          <button className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600">Rimuovi dal Carrello
          </button>
        </div>
      </div>
     ))}
   </div>
  );
};

export default Cart
