import {createContext, useReducer} from "react";
import reducer, {initialState} from "./reducer";
import products from "../components/Products";

export const StoreContextUser = createContext()
export const StoreContextUserProvider = ({children}) => {
  const [state, dispatch] = useReducer(reducer, initialState);

  const userToken = (token) => {
    dispatch({
      type: 'token',
      payload: token
    })
  };
  const showProducts = (products) => {
    const showProd = products.map(product => product);
    console.log(showProd, 'prodotti')
    if (showProd) {
      dispatch({
        type: 'all_prod',
        dispatch: showProd
      })
    }
  }

  const value = {
    token: state.token,
    userToken,
    showProducts
  }
  return (
   <StoreContextUser.Provider value={value}>
     {children}
   </StoreContextUser.Provider>
  )
}
