import Category from "./Category";
import {useParams} from "react-router-dom";
import {useEffect, useState} from "react";
import {itemsByCategory} from "../Api/ItemsByCategory";

const Categories = () => {
  const {id} = useParams()
  const [singleCategory, setSingleCategory] = useState(category)
  useEffect(() => {
    const fetchByCategory = async (category) => {
      //console.log(category)
      try {
        const response = await itemsByCategory(category)
        setSingleCategory(response)
      } catch (error) {
        console.log(error)
      }
    }
    fetchByCategory(singleCategory)
  }, [])
  return (
   <>
     <div className='p-3'>
       <p>pagina della singola categoria {id}</p>
     </div>
   </>
  )
}
export default Categories
