export const initialState = {
  token: null
}
const userLoginReducer = (state, action) => {
  switch (action.type) {
    case 'token':
      return {
        ...state,
        token: action.payload // lo stato diventa ciò che passiamo.
        // In questo caso diventano le nostre credenziali.
      }
    default:
      return state
  }
}
export default userLoginReducer;
