import {createContext, useReducer} from "react";
import reducer, {initialState} from "./reducer";
import {allProducts} from "../Api/allProducts";

export const StoreContextUser = createContext()
export const StoreContextUserProvider = ({children}) => {
  const [state, dispatch] = useReducer(reducer, initialState);

  const userToken = (token) => {
    dispatch({
      type: 'token',
      payload: token
    })
  };
  //Richiamare api dal context
  const allProd = async () => {
    try {
      const getAllProd = await allProducts();

      if (getAllProd.ok) {
        const data = await getAllProd.json(); // Attendere la risoluzione della promessa JSON
        console.log(data);

        dispatch({
          type: 'all_prod',
          payload: data
        });
      } else {
        console.error('Errore nella richiesta API:', getAllProd.status, getAllProd.statusText);
      }
    } catch (error) {
      console.error('Errore durante la richiesta API:', error);
    }
  };

  const value = {
    token: state.token,
    products: state.products,
    userToken,
    allProd

  }
  return (
   <StoreContextUser.Provider value={value}>
     {children}
   </StoreContextUser.Provider>
  )
}
