import Logo from '../Assets/logo.png'
import {PhoneIcon, UserIcon} from '@heroicons/react/24/outline'
import {ShoppingBagIcon} from "@heroicons/react/24/outline";
import {HeartIcon} from "@heroicons/react/24/outline";
import SubMenu from "./SubMenu";
import {Link} from "react-router-dom";
import {allProducts} from "../Api/allProducts";
import SearchProducts from "./SearchProducts";
import {useEffect, useState} from "react";


const NavBar = () => {
  const {products} = allProducts(); // array di prodotti
  const [filter, setFilter] = useState('');
  const [filtered, setFiltered] = useState([]);

  useEffect(() => {
    const _filter = filter.toLowerCase().trim();
    let filtered = [...products];

    if (_filter.length > 0) {
      filtered = filtered.filter(el => `${el.title}`.toLowerCase().indexOf(_filter) > -1);
    }
    setFiltered(filtered);
  }, [products, filter, setFiltered]);

  return (
   <>
     <div className='flex items-center'>
       <div className='flex flex-1'>
         <Link to='/'>
           <img className='w-12 h-auto mr-auto' src={Logo} alt="Logo e commerce"/>
         </Link>
       </div>

       <div className='flex items-center'>
         <SearchProducts
          filter={filter}
          setFilter={setFilter}
          filtered={filtered}
         />
         <div className='flex items-center space-x-4'>
           <UserIcon className='w-5 h-5 hover:cursor-pointer'/>
           <ShoppingBagIcon className='w-5 h-5 hover:cursor-pointer'/>
           <HeartIcon className='w-5 h-5 hover:cursor-pointer'/>
           <PhoneIcon className='w-5 h-5 hover:cursor-pointer'/>
         </div>
       </div>
     </div>
     <div className='flex text-xs list-none justify-center space-x-2 pt-2 cursor-pointer'>
       <SubMenu/>
       {/*{filtered && filtered.map(filteredProduct => <Product product={filteredProduct}/>)}*/}
     </div>
   </>
  )
}

export default NavBar
