import {Link, useParams,} from "react-router-dom";
import {useEffect, useState} from "react";
import {itemsByCategory} from "../Api/ItemsByCategory";
import categories from "../Pages/Categories";

const Category = ({category}) => {
  const {id} = useParams()

  const [singleCategory, setSingleCategory] = useState(category)
  useEffect(() => {
    const fetchByCategory = async (category) => {
      //console.log(category)
      try {
        const response = await itemsByCategory(category)
        console.log(response)
        setSingleCategory(response)
      }catch (error) {
        console.log(error)
      }
    }
    fetchByCategory(singleCategory)
  },[])
  return (

   <Link  to={`/categories/${category}`}>
     {category}
   </Link>
  );
};

export default Category;
