import Logo from '../Assets/logo.png'
import {PhoneIcon, UserIcon} from '@heroicons/react/24/outline'
import {ShoppingBagIcon} from "@heroicons/react/24/outline";
import {HeartIcon} from "@heroicons/react/24/outline";
import SubMenu from "./SubMenu";
const NavBar = () => {
  // Richiamare l'api per fetchare le categorie passando come props la singola categoria creando di conseguenza
  //le voci del submenu
  return (
   <>

   <div className='flex items-center'>
     {/* Logo */}
     <div className='flex flex-1'>
       <img className='w-12 h-auto mr-auto' src={Logo} alt="Logo e commerce"/>
     </div>
     {/*  - Ricerca  */}
     <div className='flex items-center'>
       <form className='border-b p-2 mr-2'>
         <input type="text" placeholder='Cerca prodotti o articoli' className='outline-none'/>
       </form>
       {/*  - Icone  */}
       <div className='flex items-center space-x-4'>
        <UserIcon className='w-5 h-5 hover:cursor-pointer'/>
         <ShoppingBagIcon className='w-5 h-5 hover:cursor-pointer'/>
         <HeartIcon className='w-5 h-5 hover:cursor-pointer'/>
         <PhoneIcon className='w-5 h-5 hover:cursor-pointer'/>
       </div>
     </div>
   </div>
     <div className='flex text-xs list-none justify-center space-x-2 pt-2 cursor-pointer'>
       <SubMenu/>
     </div>

   </>
  )
}

export default NavBar
