import Category from "./Category";
import {useParams} from "react-router-dom";
import {useEffect, useState} from "react";
import {itemsByCategory} from "../Api/ItemsByCategory";
import Product from "../components/Product";
import Loading from "../components/Loading";

const Categories = () => {
  const {id} = useParams()
  const [singleCategory, setSingleCategory] = useState(id)
  const [isLoading, setIsLoading] = useState(false)
  useEffect(() => {
    setIsLoading(true)
    const fetchByCategory = async (category) => {
      console.log(category)
      try {
        const response = await itemsByCategory(id)
        setSingleCategory(response)
        setIsLoading(false)
      } catch (error) {
        setIsLoading(true)
        console.log(error)
      }
    }
    fetchByCategory(singleCategory)
  }, [id])
  return (
   <>
     {isLoading && <Loading/>}
     <div className='grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 gap-4 mt-2'>
       {singleCategory && singleCategory.data?.map(category => <Product product={category}/>)}
     </div>
   </>
  )
}
export default Categories
