import {createContext, useReducer} from "react";
import reducer,{initialLoginState} from "./reducer";
import {authForm} from "../Api/authForm";
export const StoreContextUser = createContext()
export const StoreContextUserProvider = ({children}) => {
  const [state,dispatch] = useReducer(reducer,initialLoginState);

  const userLogin = async (token) => {
    console.log(token)
    try {
       dispatch({
         type:'login',
         payload:token
       })

    } catch (error) {
      // Gestisci l'errore qui, ad esempio loggandolo o mostrando un messaggio all'utente
      console.log('Errore durante il login:', error);
    }
  };





  const value = {
    token:state.token,
    userLogin
  }
    return (
     <StoreContextUser.Provider value={value}>
       {children}
     </StoreContextUser.Provider>
    )
}
