import {useState} from "react";
import {useContext} from "react";
import {authForm} from "../Api/authForm";
import {StoreContextUser} from "../Contenxt/authContext";
import { useNavigate } from "react-router-dom"


const LoginForm = () => {
  const [credential, setCredential] = useState({username: '', password: ''})
  const [error,setError] = useState(false)
  const {userToken} = useContext(StoreContextUser);
  const navigate = useNavigate()

  const handleChange = (e) => {
    setCredential({
      ...credential,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmitForm = async (e) => {
    e.preventDefault();
    try {
      const req = await authForm(credential.username, credential.password);
      if(req.status === 200){
        setError(false)
        const token = await req.data
        userToken(token)
        navigate('/')
      }
    } catch (e) {
      // Gestisci l'errore qui, ad esempio loggandolo
      console.log(e,'eeeee') // errore del server
      if(e.response.status !== 200){
        setError(true)
      }
    }

  };

  return (
   <>
     <div className="md:mx-auto max-w-2xl bg-white p-8 rounded shadow-md">
       <h2 className="text-2xl font-semibold mb-2 text-center">Accedi</h2>
       <p className='text-center mb-2'>Inserisci i dati di accesso per poter effettuare il login.</p>
       <form onSubmit={handleSubmitForm}>

         <div className="mb-4">
           <label className="block text-gray-700 text-sm font-bold mb-2">Username:</label>
           <input type="text" value={credential.username} onChange={handleChange} name="username"
                  className="w-full p-2 border border-gray-300 rounded"/>
         </div>

         <div className="mb-6">
           <label className="block text-gray-700 text-sm font-bold mb-2">Password:</label>
           <input type="password" value={credential.password} onChange={handleChange} name="password"
                  className="w-full p-2 border border-gray-300 rounded"/>
         </div>

         <button type="submit"
                 className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600">Accedi
         </button>
         {error ? <p>Ricontrolla le credenziali di accesso! Qualcosa Ã¨ andato storto!</p> : null}
       </form>
     </div>
   </>
  )
}
export default LoginForm
